import React from "react";

function CreateArea(props) {

  const[heading,setHeading]=React.useState("");
  const[content,setContent]=React.useState("");


    function handleHeading(event){
      setHeading(event.target.title)
      console.log(event.target.title);
    }
    function handleContent(event){
      setContent(event.target.Content)
      console.log(event.target.Content);
    }



  return (
    <div>
      <form>
        <input onChange={handleHeading} name="title" placeholder="Title" value={heading} />
        <textarea onChange={handleContent} name="content" placeholder="Take a note..." rows="3" value={content} />
        <button>Add</button>
      </form>
    </div>
  );
}

export default CreateArea;
